var ballColor = '#ffffff';
var textColor = '#ffffff';
var textColor2 = '#fff159'
var paddleColor = '#42f5ec';
var brickColor = '#f59342';

var canvas = document.getElementById("myCanvas");
var ctx = canvas.getContext("2d");
var ballRadius = 10;
var x = canvas.width/2;
var y = canvas.height-30;
var dx = 2;
var dy = -2;
var paddleHeight = 10;
var paddleWidth = 100;
var paddleX = (canvas.width-paddleWidth)/2;
var rightPressed = false;
var leftPressed = false;
var brickRowCount = 4;
var brickColumnCount = 6;
var brickWidth = 80;
var brickHeight = 20;
var brickPadding = 30;
var brickOffsetTop = 60;
var brickOffsetLeft = 120;
var score = 0;
var lives = 3;

var bricks = [];
for(var c=0; c<brickColumnCount; c++) {
    bricks[c] = [];
    for(var r=0; r<brickRowCount; r++) {
        bricks[c][r] = { x: 0, y: 0, status: 1 };
    }
}

document.addEventListener("keydown", keyDownHandler, false);
document.addEventListener("keyup", keyUpHandler, false);
document.addEventListener("mousemove", mouseMoveHandler, false);

function keyDownHandler(e) {
    if(e.key == "Right" || e.key == "ArrowRight") {
        rightPressed = true;
    }
    else if(e.key == "Left" || e.key == "ArrowLeft") {
        leftPressed = true;
    }
    if(e.key == "Enter") {
        isGameOver = false;
    }
}

function keyUpHandler(e) {
    if(e.key == "Right" || e.key == "ArrowRight") {
        rightPressed = false;
    }
    else if(e.key == "Left" || e.key == "ArrowLeft") {
        leftPressed = false;
    }
}

function mouseMoveHandler(e) {
    var relativeX = e.clientX - canvas.offsetLeft;
    if(relativeX > 0 && relativeX < canvas.width) {
        paddleX = relativeX - paddleWidth/2;
    }
}

function drawBall() {
    ctx.beginPath();
    ctx.arc(x, y, ballRadius, 0, Math.PI*2);
    ctx.fillStyle = ballColor;
    ctx.fill();
    ctx.closePath();
}
function drawPaddle() {
    ctx.beginPath();
    ctx.rect(paddleX, canvas.height-paddleHeight, paddleWidth, paddleHeight);
    ctx.fillStyle = paddleColor;
    ctx.fill();
    ctx.closePath();
} 
function drawBricks() {
    for(var c=0; c<brickColumnCount; c++) {
        for(var r=0; r<brickRowCount; r++) {
            if(bricks[c][r].status == 1) {
                var brickX = (c*(brickWidth+brickPadding))+brickOffsetLeft;
                var brickY = (r*(brickHeight+brickPadding))+brickOffsetTop;
                bricks[c][r].x = brickX;
                bricks[c][r].y = brickY;
                ctx.beginPath();
                ctx.rect(brickX, brickY, brickWidth, brickHeight);
                ctx.fillStyle = brickColor;
                ctx.fill();
                ctx.closePath();
            }
        }
    }
}
function collisionDetection() {
    for(var c=0; c<brickColumnCount; c++) {
        for(var r=0; r<brickRowCount; r++) {
            var b = bricks[c][r];
            if(b.status == 1) {
                if(x > b.x && x < b.x+brickWidth && y > b.y && y < b.y+brickHeight) {
                    dy = -dy;
                    b.status = 0;
                    score++;             
                    beep();
                    if(score == brickRowCount*brickColumnCount) {
                        gameOver("Congrats! You Won");
                    }
                }
            }
        }
    }
}
function drawScore() {
    ctx.font = "16px Arial";
    ctx.fillStyle = textColor;
    ctx.fillText("Score: "+score, 8, 20);
}
function drawLives() {
    ctx.font = "16px Arial";
    ctx.fillStyle = textColor;
    ctx.fillText("Lives: "+lives, canvas.width-65, 20);
}
function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play().catch(function(err) {
        console.log('play failed', err.message);
    });
}
isGameOver = false;
function gameOver(text) {
    isGameOver = true;
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    
    ctx.font = "46px Arial";
    ctx.fillStyle = textColor;
    ctx.fillText(text, canvas.width / 2  - 150, canvas.height / 2 - 50);

    ctx.font = "18px Arial";
    ctx.fillStyle = textColor2
    ctx.fillText("Press Enter to start over", canvas.width / 2  - 100, canvas.height / 2 + 50);

    waitForToReload();
}
function waitForToReload() {
    if(isGameOver) {
        setTimeout(waitForToReload, 500); // check again in a 1/2 second  
    }
    else {
        document.location.reload();
    }
}

function draw() {
    if (isGameOver) {
        return;
    }

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    drawBricks();
    drawBall();
    drawPaddle();

    drawScore();
    drawLives();
    
    collisionDetection();

    if(x + dx > canvas.width-ballRadius || x + dx < ballRadius) {
        dx = -dx;
        beep();
    }
    
    if(y + dy < ballRadius) {
        dy = -dy;
        beep();
    }
    else if(y + dy > canvas.height-ballRadius) {
        if(x > paddleX  && x < paddleX + paddleWidth) {
            dy = -dy;
            
            // change direction based on previous dir and location of ball
            if (x < paddleX + (paddleWidth / 2)) {
                if (dx > 0) {
                    dx = -dx;
                }
            }
            else {
                if (dx < 0) {
                    dx = -dx;
                }
            }

            beep();
        }
        else {
            lives--;
            if(!lives) {
                gameOver("GAME OVER");
            }
            else {
                x = canvas.width/2;
                y = canvas.height-30;
                dx = 3;
                dy = -3;
                paddleX = (canvas.width-paddleWidth)/2;
            }
        }
    }

    if(rightPressed && paddleX < canvas.width-paddleWidth) {
        paddleX += 7;
    }
    else if(leftPressed && paddleX > 0) {
        paddleX -= 7;
    }
    
    x += dx;
    y += dy;
}

function animate() {
    //requestAnimationFrame(animate);
    draw();

    // calc elapsed time since last loop
    now = Date.now();
    elapsed = now - then;

    console.log('elapse, fpsInterval', elapsed, fpsInterval);
    // if enough time has elapsed, draw the next frame
    if (elapsed > fpsInterval) {

        // Get ready for next frame by setting then=now, but also adjust for your
        // specified fpsInterval not being a multiple of RAF's interval (16.7ms)
        then = now - (elapsed % fpsInterval);

        // Put your drawing code here
        draw();
    }
    setTimeout(animate, 10);    
}

var fpsInterval, startTime, now, then, elapsed;
fps = 480;
fpsInterval = 6000 / fps;
then = Date.now();
startTime = then;
animate();